mapper.select:
select count(*) from news


        AND title LIKE concat ('%',#{title},'%')


        AND status = #{status}


        and post_time > #{start}


        and post_time < #{end}

select news.id, title, content, content_html as contentHtml, image, post_time as postTime, like_count as likeCount, comment_count as commentCount, category_id as categoryId, user_id as userId, u.name as authorName, news.status, is_delete as isDelete, audit, news_category.name as categoryName from news, news_category, user u
category_id = news_category.id and user_id = u.id
AND title LIKE concat ('%',#{title},'%')
AND news.status = #{status}
and post_time > #{start}
and post_time < #{end}
limit #{page}, #{limit}
